INSERT INTO cartaTipos
VALUES(200, 'CR057_COMPROMISOEFECTOSPENDIENTES', '<p><span style="font-family: verdana; font-size: 8pt;">Al titular se le girará en la cuenta que tiene domiciliada las cantidades arriba indicadas para la cancelación de la deuda, por los importes y fechas especificados en este aplazamiento, realizando los pagos dentro de los días del mes especificados. Le informamos que el impago de cualquiera de los plazos firmados en este acuerdo, implicaría que el importe de la deuda fuera remitido a recaudación ejecutiva por parte del órgano de Hacienda del <em>Excelentísimo Ayuntamiento de Guadalajara</em> mediante el procedimiento de apremio, que sería llevado a cabo de acuerdo con el <em>Artículo 99</em> de la <em>Ordenanza Fiscal General de Gestión, Recaudación e Inspección </em>del <em>Excelentísimo Ayuntamiento de Guadalajara</em>. El procedimiento decobro está regulado por el <em>Reglamento General de Recaudación</em>, en el que se establece el importe de los intereses de demora aplicables y las costas del procedimiento.</span></p>'
, 0, 'NOT', GETDATE(), 'gmdesousa', NULL, NULL, 'Compromiso de Efectos Pdtes.')

